mapper.insert:
		 INSERT MOZ_ATCH_FILE(
                FILE_IDX
                , ATCH_IDX
                , OPRTR_ID
                , FILE_ORG_NM
                , FILE_SAVE_NM
                , FILE_PATH
                , FILE_SIZE
                , FILE_EXTS
                , CR_DT
                , FILE_TYPE
            )VALUES(
            	#{fileIdx}
                , #{atchIdx}
                , #{oprtrId}
                , #{fileOrgNm}
                , #{fileSaveNm}
                , #{filePath}
                , #{fileSize}
                , #{fileExts}
                , NOW()
                , #{fileType}
            )

mapper.update:
		UPDATE  MOZ_ATCH_FILE
        SET     FILE_ORG_NM = #{fileOrgNm}
                , FILE_SAVE_NM = #{fileSaveNm}
                , FILE_PATH = #{filePath}
                , FILE_SIZE = #{fileSize}
                , FILE_EXTS = #{fileExts}
        WHERE   FILE_IDX = #{fileIdx}
