mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY bt.CR_DT) AS rnum , bt.BOARD_IDX , bt.BOARD_CD_ID , bt.OPRTR_ID , bt.WRTR_NM , bt.BOARD_TITLE , bt.BOARD_CONTENTS , bt.VIEW_CNT , bt.USE_YN , bt.POST_STR_DT , bt.POST_END_DT , bt.IMPRT_YN , bt.CR_DT FROM MOZ_BRD bt
AND bt.CATE_CD = #{cateCd}
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}

        SELECT  COUNT(*) AS "cnt"
        FROM    MOZ_BRD bt


	        AND bt.CATE_CD = #{cateCd}

mapper.insert:
INSERT INTO MOZ_BRD
    (
        BOARD_IDX
        , BOARD_TITLE
        , BOARD_CONTENTS
        , VIEW_CNT
        , CR_DT
        , UPD_DT
        , OPRTR_ID
        , WRTR_NM
        , POST_STR_DT
        , POST_END_DT
        , USE_YN
        , POPUP_YN
        , IMPRT_YN
        , CATE_CD
    )
VALUES
    (
        #{boardIdx}
        , #{boardTitle}
        , #{boardContents}
        , '0'
        , NOW()
        , NOW()
        , #{oprtrId}
        , #{wrtrNm}
        , #{postStrDtString}
        , #{postEndDtString}
        , #{useYn}
        , #{popupYn}
        , #{imprtYn}
        , #{cateCd}
    )

mapper.update:
	UPDATE  MOZ_BRD
  SET     BOARD_CD_ID=#{boardCdId}
          , BOARD_TITLE=#{boardTitle}
          , BOARD_CONTENTS=#{boardContents}
          , UPD_DT=NOW()
          , VIEW_CNT=#{viewCnt}
          , POST_STR_DT=#{postStrDtString}
          , POST_END_DT=#{postEndDtString}
          , USE_YN=#{useYn}
          , POPUP_YN=#{popupYn}
          , IMPRT_YN=#{imprtYn}
  WHERE   BOARD_IDX=#{boardIdx}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY MB.CR_DT) AS rnum , MB.BOARD_IDX , MB.BOARD_TITLE , MB.VIEW_CNT , MB.IMPRT_YN , MB.WRTR_NM , MB.CR_DT FROM MOZ_BRD MB WHERE 1=1
AND USE_YN = #{useYn}
AND CATE_CD = #{cateCd}
AND MB.BOARD_TITLE LIKE CONCAT('%', #{searchTxt}, '%')
AND (MB.POST_STR_DT <= CURDATE() OR MB.POST_STR_DT IS NULL) AND (MB.POST_END_DT >= CURDATE() OR MB.POST_END_DT IS NULL)
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}

SELECT COUNT(*) FROM MOZ_BRD MB WHERE 1 = 1
AND USE_YN = #{useYn}
AND CATE_CD = #{cateCd}
AND MB.BOARD_TITLE LIKE CONCAT('%', #{searchTxt}, '%')
AND (MB.POST_STR_DT <= CURDATE() OR MB.POST_STR_DT IS NULL) AND (MB.POST_END_DT >= CURDATE() OR MB.POST_END_DT IS NULL)

SELECT MB.BOARD_TITLE , MB.BOARD_CONTENTS , MB.VIEW_CNT , MB.CR_DT , MWO.OPRTR_NM , MWO.OPRTR_DEPT_NM FROM MOZ_BRD MB INNER JOIN MOZ_WEB_OPRTR MWO ON MB.OPRTR_ID = MWO.OPRTR_ID WHERE MB.USE_YN = #{useYn} AND MB.CATE_CD = #{cateCd}
AND (MB.POST_STR_DT <= CURDATE() OR MB.POST_STR_DT IS NULL) AND (MB.POST_END_DT >= CURDATE() OR MB.POST_END_DT IS NULL)
AND MB.BOARD_IDX = #{boardIdx}

mapper.update:
UPDATE MOZ_BRD 
SET VIEW_CNT = VIEW_CNT + 1
WHERE BOARD_IDX = #{boardIdx}
