mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY ft.REG_DT) AS rnum , ft.FAQ_IDX , ft.POST_TITLE , ft.REG_DT , ft.WRTR_ID , ft.VIEW_CNT , ft.FAQ_SEQ , ft.FAQ_KYWRD , cct.CD_NM , wot.OPRTR_ACCOUNT_ID FROM MOZ_FAQ ft LEFT JOIN MOZ_CM_CD cct ON ft.CATE_TY = cct.CD_ID LEFT JOIN MOZ_WEB_OPRTR wot ON ft.WRTR_ID = wot.OPRTR_ID
AND ft.DEL_YN = 'N'
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}

mapper.insert:
INSERT INTO MOZ_FAQ
    (
        FAQ_IDX
        , FAQ_SEQ
        , CATE_TY
        , WRTR_ID
        , POST_TITLE
        , POST_CONTENT
        , REG_DT
        , DEL_YN
        , VIEW_CNT
        , FAQ_KYWRD
    )
VALUES
    (
        #{faqIdx}
       , #{faqSeq}
       , #{cateTy}
       , #{wrtrId}
       , #{postTitle}
       , #{postContent}
       , NOW()
       , 'N'
       , '0'
       , #{faqKywrd}
    )

mapper.update:
UPDATE  MOZ_FAQ
SET     FAQ_SEQ = #{faqSeq}
        , CATE_TY = #{cateTy}
        , POST_TITLE = #{postTitle}
        , POST_CONTENT = #{postContent}
        , FAQ_KYWRD = #{faqKywrd}
        , MDF_DT = NOW()
WHERE   FAQ_IDX = #{faqIdx}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY MF.REG_DT) AS rnum , MF.FAQ_IDX , MF.POST_TITLE , MF.REG_DT , MF.VIEW_CNT FROM MOZ_FAQ MF WHERE MF.DEL_YN = 'N'
AND (POST_TITLE LIKE CONCAT('%', #{searchTxt}, '%') OR FAQ_KYWRD LIKE CONCAT('%', #{searchTxt}, '%'))
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}

mapper.update:
UPDATE MOZ_FAQ
SET VIEW_CNT = VIEW_CNT + 1
WHERE FAQ_IDX = #{faqIdx}
