mapper.update:
		UPDATE  MOZ_TFC_SFTY_EDCTN
        SET     POST_TTL = #{postTtl}
                , POST_CN = #{postCn}
                , POST_STR_DE = #{postStrDeString}
                , POST_END_DE = #{postEndDeString}
                , EXP_YN = #{expYn}
                , UPD_DT = NOW()
                , UPDR = #{updr}
        WHERE   TFC_SFTY_EDCTN_ID = #{tfcSftyEdctnId}

mapper.select:
SELECT COUNT(*) FROM MOZ_TFC_SFTY_EDCTN MTSE WHERE MTSE.EXP_YN = #{expYn}
AND (MTSE.POST_STR_DE <= CURDATE() OR MTSE.POST_STR_DE IS NULL) AND (MTSE.POST_END_DE >= CURDATE() OR MTSE.POST_END_DE IS NULL)
AND MTSE.POST_TTL LIKE CONCAT('%', #{searchTxt}, '%')

SELECT ROW_NUMBER() OVER(ORDER BY MTSE.CR_DT) AS rnum , MTSE.TFC_SFTY_EDCTN_ID , MTSE.POST_TTL , MTSE.CR_DT , MTSE.VIEW_CNT FROM MOZ_TFC_SFTY_EDCTN MTSE WHERE MTSE.EXP_YN = #{expYn}
AND (MTSE.POST_STR_DE <= CURDATE() OR MTSE.POST_STR_DE IS NULL) AND (MTSE.POST_END_DE >= CURDATE() OR MTSE.POST_END_DE IS NULL)
AND MTSE.POST_TTL LIKE CONCAT('%', #{searchTxt}, '%')
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}

mapper.update:
UPDATE MOZ_TFC_SFTY_EDCTN
SET VIEW_CNT = VIEW_CNT + 1
WHERE EXP_YN = #{expYn}

AND (POST_STR_DE <= CURDATE() OR POST_STR_DE IS NULL)
AND (POST_END_DE >= CURDATE() OR POST_END_DE IS NULL)

AND TFC_SFTY_EDCTN_ID = #{tfcSftyEdctnId}

mapper.select:
SELECT MTSE.POST_TTL ,MTSE.POST_CN , MTSE.VIEW_CNT , MTSE.CR_DT , MWO.OPRTR_NM , MWO.OPRTR_DEPT_NM FROM MOZ_TFC_SFTY_EDCTN MTSE INNER JOIN MOZ_WEB_OPRTR MWO ON MTSE.CRTR = MWO.OPRTR_ID WHERE MTSE.EXP_YN = #{expYn}
AND (MTSE.POST_STR_DE <= CURDATE() OR MTSE.POST_STR_DE IS NULL) AND (MTSE.POST_END_DE >= CURDATE() OR MTSE.POST_END_DE IS NULL)
AND MTSE.TFC_SFTY_EDCTN_ID = #{tfcSftyEdctnId}
