mapper.select:
SELECT * 
FROM MOZ_TFC_ENF_MASTER
WHERE TFC_ENF_ID = #{tfcEnfId}

SELECT GROUP_CONCAT(A.SEQ_CNT ORDER BY A.GRAPH_TIME) AS COUNT , GROUP_CONCAT(A.GRAPH_TIME ORDER BY A.GRAPH_TIME) AS LABEL FROM( SELECT TIME_DATA.GRAPH_TIME ,COALESCE(GRAPH_DATA.SEQ_CNT,0) as SEQ_CNT FROM(
SELECT DATE_FORMAT(m1, '%Y-%m') AS GRAPH_TIME FROM( SELECT (#{sDate} - INTERVAL DAYOFMONTH(#{sDate})-1 DAY) +INTERVAL m MONTH as m1 FROM( SELECT @rownum:=@rownum+1 as m FROM (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t1, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t2, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t3, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t4, (SELECT @rownum:=-1) t0 ) d1 ) d2 WHERE m1 <= #{eDate} ORDER BY m1
SELECT DATE_ADD(#{sDate}, INTERVAL seq.seq DAY) AS GRAPH_TIME FROM ( SELECT (t3*100 + t2*10 + t1) AS seq FROM (SELECT 0 t1 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t1, (SELECT 0 t2 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t2, (SELECT 0 t3 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t3 ) AS seq WHERE DATE_ADD(#{sDate}, INTERVAL seq.seq DAY) BETWEEN #{sDate} AND #{eDate}
WITH RECURSIVE CTE AS ( SELECT 00 AS RNUM UNION ALL SELECT RNUM + 01 FROM CTE WHERE RNUM < 23 ) SELECT IF(RNUM < 10, CONCAT('0',RNUM) , RNUM) AS GRAPH_TIME FROM CTE
)TIME_DATA LEFT JOIN ( SELECT COUNT(TEMT.TFC_ENF_ID) AS SEQ_CNT
, DATE_FORMAT(TEMT.TFC_ENF_DT, '%Y-%m') as TIME_DATA
, DATE_FORMAT(TEMT.TFC_ENF_DT, '%Y-%m-%d') as TIME_DATA
, DATE_FORMAT(TEMT.TFC_ENF_DT, '%H') as TIME_DATA
FROM MOZ_TFC_ENF_MASTER TEMT LEFT JOIN MOZ_TFC_ENF_FINE_INFO MTEFI ON TEMT.TFC_ENF_ID = MTEFI.TFC_ENF_ID LEFT JOIN MOZ_TFC_LW_FINE_INFO TLFI ON MTEFI.TFC_LAW_FINE_ID = TLFI.TFC_LAW_FINE_ID LEFT JOIN MOZ_TFC_LW_INFO TLI ON TLFI.TFC_LAW_ID = TLI.TFC_LAW_ID WHERE 1=1
AND DATE_FORMAT(TEMT.TFC_ENF_DT, '%Y-%m') BETWEEN #{sDate} AND #{eDate}
AND DATE_FORMAT(TEMT.TFC_ENF_DT, '%Y-%m-%d') BETWEEN #{sDate} AND #{eDate}
AND DATE_FORMAT(TEMT.TFC_ENF_DT, '%Y-%m-%d') = #{sDate}
AND TLI.LAW_CHAPTER = #{cate} GROUP BY TIME_DATA ) GRAPH_DATA on TIME_DATA.GRAPH_TIME = GRAPH_DATA.TIME_DATA ORDER BY TIME_DATA.GRAPH_TIME ASC )A

SELECT TEMT.TFC_ENF_ID, TEMT.VIO_ID, TEMT.TFC_LAW_ID, TEMT.TFC_ENF_EQP_ID, TEMT.POL_ID, TEMT.VIO_FILE_NO, TEMT.TFC_ENF_DT, TEMT.ROAD_ADDR, TEMT.ROAD_LN, TEMT.VH_REG_NO, TEMT.VH_TY, TEMT.SPD_LMT, TEMT.VIO_SPD, TEMT.OVER_SPD, TEMT.LAST_TFC_ENF_PROC_CD, TEMT.LAT, TEMT.LNG, TLIT.LAW_NM, FPIT.PYMNT_STTS , FPIT.PYMNT_ID, FPIT.PAYER_NM, FPIT.PYMNT_PRICE, FPIT.PYMNT_DDLN, FPIT.PYMNT_METHOD FROM MOZ_TFC_ENF_MASTER TEMT INNER JOIN MOZ_TFC_LW_INFO TLIT ON TEMT.TFC_LAW_ID = TLIT.TFC_LAW_ID INNER JOIN MOZ_FINE_PYMNT_INFO FPIT ON TEMT.TFC_ENF_ID = FPIT.TFC_ENF_ID INNER JOIN MOZ_VIO_INFO VIT ON TEMT.VIO_ID = VIT.VIO_ID WHERE 1 = 1
AND FPIT.PYMNT_STTS = #{pymntStts}
AND FPIT.PYMNT_ID = #{pymntId}
AND VIT.DVR_LCEN_ID = #{dvrLcenId}
AND VIT.VIO_NM = #{vioNm}
AND TEMT.TFC_ENF_ID = #{tfcEnfId}

mapper.update:
UPDATE  MOZ_TFC_ENF_MASTER
SET     ROAD_ADDR = #{roadAddr}

            , TFC_ENF_INFOIMAGE = #{tfcEnfInfoimage}
            , TFC_ENF_IMAGEPATH = #{tfcEnfImagepath}

        , LAST_TFC_ENF_PROC_CD = #{lastTfcEnfProcCd}
        , LAT = #{lat}
        , LNG = #{lng}
        , VH_REG_NO = #{vhRegNo}
        , VH_TY = #{vhTy}
        , TFC_ENF_TTL = #{tfcEnfTtl}
        , TFC_ENF_DT = #{tfcEnfDt}
WHERE   TFC_ENF_ID = #{tfcEnfId}

    	UPDATE MOZ_TFC_ENF_MASTER
    		SET  TFC_ENF_DT = #{tfcEnfDt}
				, ROAD_ADDR = #{roadAddr}
				, VH_REG_NO = #{vhRegNo}
				, CR_DT = NOW()
		WHERE TFC_ENF_ID = #{tfcEnfId}

UPDATE MOZ_TFC_ENF_MASTER
	SET	VIO_ID = #{tfcEnfMaster.vioId}
		, TFC_ENF_DT = TO_DATE(#{tfcEnfMaster.tfcEnfDt},'YYYY-MM-DD HH24:MI:SS')
		, ROAD_ADDR = #{tfcEnfMaster.roadAddr}
		, VH_REG_NO = #{tfcEnfMaster.vhRegNo}
		, CR_DT = NOW()
WHERE TFC_ENF_ID = #{tfcEnfId}

 	UPDATE MOZ_TFC_ENF_MASTER
	 	SET    TFC_ENF_IMAGEPATH = #{tfcEnfImagePath},
 	   	   TFC_ENF_INFOIMAGE = #{tfcEnfInfoImage}
		WHERE  TFC_ENF_ID = #{tfcEnfId}

UPDATE MOZ_TFC_ENF_MASTER
SET DEL_YN = #{delYn}
WHERE TFC_ENF_ID = #{tfcEnfId}

mapper.select:
		SELECT COUNT(*) AS "cnt"
		FROM MOZ_TFC_ENF_MASTER mtfm
		WHERE 1=1






               AND DATE_FORMAT(mtfm.TFC_ENF_DT, '%Y-%m-%d') >= #{sDate}
               AND DATE_FORMAT(mtfm.TFC_ENF_DT, '%Y-%m-%d') <= #{eDate}

SELECT COUNT(*) FROM MOZ_TFC_ENF_MASTER MTEM INNER JOIN ( SELECT MTEFI.TFC_ENF_ID , count(*) AS enfFineInfoCnt , CONCAT( '[', SUBSTRING_INDEX(GROUP_CONCAT(MTLI.LAW_TYPE ORDER BY MTLI.LAW_ARTICLE_NO), ',', 1), '] ' , SUBSTRING_INDEX(GROUP_CONCAT(MTLI.LAW_NM ORDER BY MTLI.LAW_ARTICLE_NO), ',', 1) ) AS title FROM MOZ_TFC_ENF_FINE_INFO MTEFI INNER JOIN MOZ_TFC_LW_FINE_INFO MTLFI ON MTLFI.TFC_LAW_FINE_ID = MTEFI.TFC_LAW_FINE_ID INNER JOIN MOZ_TFC_LW_INFO MTLI ON MTLI.TFC_LAW_ID = MTEFI.TFC_LAW_ID GROUP BY MTEFI.TFC_ENF_ID ) TITLE_AND_CNT ON TITLE_AND_CNT.TFC_ENF_ID = MTEM.TFC_ENF_ID
AND MTEM.TFC_ENF_DT >= #{sDate} AND MTEM.TFC_ENF_DT <= #{eDate}
AND MTEM.TFC_ENF_ID = #{searchTxt}

SELECT ROW_NUMBER() OVER(ORDER BY MTEM.TFC_ENF_DT) AS rnum , MTEM.TFC_ENF_ID , MTEM.TFC_ENF_DT , TITLE_AND_CNT.title , TITLE_AND_CNT.enfFineInfoCnt FROM MOZ_TFC_ENF_MASTER MTEM INNER JOIN ( SELECT MTEFI.TFC_ENF_ID , count(*) AS enfFineInfoCnt , CONCAT( '[', SUBSTRING_INDEX(GROUP_CONCAT(MCC.CD_NM ORDER BY MTLI.LAW_ARTICLE_NO), ',', 1), '] ' , SUBSTRING_INDEX(GROUP_CONCAT(MTLI.LAW_NM ORDER BY MTLI.LAW_ARTICLE_NO), ',', 1) ) AS title FROM MOZ_TFC_ENF_FINE_INFO MTEFI INNER JOIN MOZ_TFC_LW_FINE_INFO MTLFI ON MTLFI.TFC_LAW_FINE_ID = MTEFI.TFC_LAW_FINE_ID INNER JOIN MOZ_TFC_LW_INFO MTLI ON MTLI.TFC_LAW_ID = MTEFI.TFC_LAW_ID INNER JOIN MOZ_CM_CD MCC ON MTLI.LAW_TYPE = MCC.CD_ID GROUP BY MTEFI.TFC_ENF_ID ) TITLE_AND_CNT ON TITLE_AND_CNT.TFC_ENF_ID = MTEM.TFC_ENF_ID
AND MTEM.TFC_ENF_DT >= #{sDate} AND MTEM.TFC_ENF_DT <= #{eDate}
AND MTEM.TFC_ENF_ID = #{searchTxt}
ORDER BY rnum DESC LIMIT #{length} OFFSET #{start}
