mapper.select:
SELECT  COUNT(*)    AS "cnt"
FROM    MOZ_TFC_ENF_EQP_MASTER
WHERE   TFC_ENF_EQP_ID = #{tfcEnfEqpId}

SELECT GROUP_CONCAT(A.SEQ_CNT ORDER BY A.GRAPH_TIME) AS COUNT , GROUP_CONCAT(A.GRAPH_TIME ORDER BY A.GRAPH_TIME) AS LABEL FROM( SELECT TIME_DATA.GRAPH_TIME ,COALESCE(GRAPH_DATA.SEQ_CNT,0) as SEQ_CNT FROM(
SELECT DATE_FORMAT(m1, '%Y-%m') AS GRAPH_TIME FROM( SELECT (#{sDate} - INTERVAL DAYOFMONTH(#{sDate})-1 DAY) +INTERVAL m MONTH as m1 FROM( SELECT @rownum:=@rownum+1 as m FROM (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t1, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t2, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t3, (SELECT 1 union SELECT 2 union SELECT 3 union SELECT 4) t4, (SELECT @rownum:=-1) t0 ) d1 ) d2 WHERE m1 <= #{eDate} ORDER BY m1
SELECT DATE_ADD(#{sDate}, INTERVAL seq.seq DAY) AS GRAPH_TIME FROM ( SELECT (t3*100 + t2*10 + t1) AS seq FROM (SELECT 0 t1 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t1, (SELECT 0 t2 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t2, (SELECT 0 t3 UNION ALL SELECT 1 UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9) t3 ) AS seq WHERE DATE_ADD(#{sDate}, INTERVAL seq.seq DAY) BETWEEN #{sDate} AND #{eDate}
WITH RECURSIVE CTE AS ( SELECT 00 AS RNUM UNION ALL SELECT RNUM + 01 FROM CTE WHERE RNUM < 23 ) SELECT IF(RNUM < 10, CONCAT('0',RNUM) , RNUM) AS GRAPH_TIME FROM CTE
)TIME_DATA LEFT JOIN ( SELECT COUNT(TEEM.TFC_ENF_EQP_ID) AS SEQ_CNT
, DATE_FORMAT(TEEM.CR_DT, '%Y-%m') as TIME_DATA
, DATE_FORMAT(TEEM.CR_DT, '%Y-%m-%d') as TIME_DATA
, DATE_FORMAT(TEEM.CR_DT, '%H') as TIME_DATA
FROM MOZ_TFC_ENF_EQP_MASTER TEEM WHERE 1=1
AND DATE_FORMAT(TEEM.CR_DT, '%Y-%m') BETWEEN #{sDate} AND #{eDate}
AND DATE_FORMAT(TEEM.CR_DT, '%Y-%m-%d') BETWEEN #{sDate} AND #{eDate}
AND DATE_FORMAT(TEEM.CR_DT, '%Y-%m-%d') = #{sDate}
AND TEEM.EQP_TY = #{cate} GROUP BY TIME_DATA ) GRAPH_DATA on TIME_DATA.GRAPH_TIME = GRAPH_DATA.TIME_DATA ORDER BY TIME_DATA.GRAPH_TIME ASC )A

mapper.insert:
INSERT INTO MOZ_TFC_ENF_EQP_MASTER
    (
        TFC_ENF_EQP_ID
        , ROAD_ADDR
        , LAT
        , LNG
        , USE_YN
        , EQP_TY
        , CR_DT
        , CR_OPRTR_ID
        , MNFCTR
        , CR_OPRTR_DEPT
        , TFC_ENF_EQP_INFO
        , TFC_ENF_EQP_IMAGEPATH
        , TFC_ENF_EQP_IMAGENAME
        , EQP_NM
        , MODEL_NM
        , INSTL_DATE
        , INSTLER
        , INSTL_YN
    )
VALUES
    (
        #{tfcEnfEqpId}
        , #{roadAddr}
        , #{lat}
        , #{lng}
        , 'Y'
        , #{eqpTy}
        , NOW()
        , #{crOprtrId}
        , #{mnfctr}
        , #{crOprtrDept}
        , #{tfcEnfEqpInfo}
        , #{tfcEnfEqpImagepath}
        , #{tfcEnfEqpImageorgn}
        , #{eqpNm}
        , #{modelNm}
        , #{instlDateString}
        , #{instler}
        , #{instlYn}
    )

mapper.update:
UPDATE  MOZ_TFC_ENF_EQP_MASTER
SET     ROAD_ADDR = #{roadAddr}

            , TFC_ENF_EQP_IMAGEPATH = #{tfcEnfEqpImagepath}
            , TFC_ENF_EQP_IMAGENAME = #{tfcEnfEqpImageorgn}

        , EQP_TY = #{eqpTy}
        , CR_OPRTR_ID = #{crOprtrId}
        , USE_YN = #{useYn}
        , MNFCTR = #{mnfctr}
        , CR_OPRTR_DEPT = #{crOprtrDept}
        , TFC_ENF_EQP_INFO = #{tfcEnfEqpInfo}
        , EQP_NM = #{eqpNm}
        , MODEL_NM = #{modelNm}
        , INSTL_DATE = #{instlDateString}
        , INSTLER = #{instler}
        , INSTL_YN = #{instlYn}
WHERE   TFC_ENF_EQP_ID = #{tfcEnfEqpId}

UPDATE MOZ_TFC_ENF_EQP_MASTER
SET    TFC_ENF_EQP_IMAGEPATH = #{tfcEnfEqpImagepath},
	   TFC_ENF_EQP_IMAGENAME = #{tfcEnfEqpImageorgn}
WHERE  TFC_ENF_EQP_ID = #{tfcEnfEqpId}
